const PUBGProfileStats = require('./Stats');
const PUBGProfileMatchHistory = require('./MatchHistory');

class PUBGProfile {
	constructor(content) {
		this.platformID = null;
		this.avatar = content.Avatar;
		this.region = null;
		this.name = content.PlayerName;
		this.currentSeason = content.seasonDisplay;
		this.currentSeasonRaw = content.defaultSeason;
		this.lastUpdated = content.LastUpdated;
		this.stats = content.Stats.map(stats => new PUBGProfileStats(stats));
		this.matchHistory = content.MatchHistory.map(match => new PUBGProfileMatchHistory(match));
	}
}

module.exports = PUBGProfile;
